INSERT INTO `vg_article` VALUES ('13', '1', '赚取积分', '', '', '', '赚取积分', '<p><b><span style=\"color:#666666;\">1 什么是积分？</span></b></p>\r\n<p><span style=\"color:#999999;font-size:12px;\">&nbsp;&nbsp; 积分是就是根据您在我们网站上的活跃情况，给与您的奖励，是一笔虚拟财富！</span></p>\r\n<p>&nbsp;</p>\r\n<p><b><span style=\"color:#666666;\">2 积分的用处？</span></b></p>\r\n<p><span style=\"color:#999999;font-size:12px;\">&nbsp;&nbsp; 积分可以用于兑换我们提供的精美礼品！</span></p>\r\n<p><span style=\"color:#999999;font-size:12px;\"><br />\r\n</span></p>\r\n<p></p>\r\n<p><b><span style=\"color:#666666;\">3 赚取积分的方法</span></b>？</p>\r\n<p><span style=\"color:#999999;font-size:12px;\">&nbsp;&nbsp; 赚取积分，可以按以下要点操作：</span></p>\r\n<span style=\"color:#999999;font-size:12px;\">&nbsp;&nbsp; 1、每天登录网站，积分加+21</span><span style=\"color:#999999;font-size:12px;\">；</span><br />\r\n<span style=\"color:#999999;font-size:12px;\">&nbsp;&nbsp; 2、每分享一个商品，积分+21；</span><br />\r\n<span style=\"color:#999999;font-size:12px;\">&nbsp;&nbsp; 3、新用户注册，赠送51积分；</span><br />\r\n<p><span style=\"color:#999999;font-size:12px;\">&nbsp;&nbsp; 4、网站活动获奖奖励积分</span></p>', '2012-08-04 18:57:34', '0', '0', '0', '1', '', '', '');
